def solution(stdin: str) -> str:
    def compress(text: str) -> str:
        encoded = ""
        run_length = 1
        for i, char in enumerate(text):
            if i == 0 or char!= text[i - 1]:
                encoded += char * run_length
                run_length += 1
        return encoded

    def decompress(encoded: str) -> str:
        decoded = ""
        run_length = 1
        for i, char in enumerate(encoded):
            if i == 0 or char!= encoded[i - 1]:
                decoded += char * run_length
                run_length += 1
        return decoded

    command, message = stdin.strip().split()
    if command == "E":
        return compress(message)
    elif command == "D":
        return decompress(message)
    else:
        return "Invalid input"

