def solution(stdin: str) -> str:
    m, n = map(int, stdin.split())
    clauses = []
    for _ in range(m):
        clause = list(map(int, stdin.split()))
        clauses.append(clause)

    def is_satisfiable(clauses):
        for clause in clauses:
            for literal in clause:
                if literal < 0:
                    if not clauses[~literal]:
                        return False
                elif literal >= 0:
                    if clauses[literal]:
                        return False
        return True

    for i in range(n):
        if clauses[i] not in [c for c in clauses if c!= clauses[i]]:
            return "unsatisfactory"

    if is_satisfiable(clauses):
        return "satisfactory"
    else:
        return "unsatisfactory"

