def solution(stdin: str) -> str:
    ell, n = map(int, stdin.split())

    k = 1
    while True:
        paper_cost = ell * k
        paper_needed = n
        if paper_cost < paper_needed:
            paper_needed = paper_cost

        backup_rolls = [ell * (k - i - 1) for i in range(k)]
        regular_rolls = [ell * (k - i - 1) for i in range(k, k + 1)]

        if paper_needed > 0:
            paper_needed -= paper_cost

        for roll in regular_rolls:
            if roll < paper_needed:
                paper_needed = roll
                break

        for roll in backup_rolls:
            if roll < paper_needed:
                paper_needed = roll
                break

        if paper_needed == 0:
            return str(k)

        k += 1

def solution(stdin: str) -> str:
    N, P, Q = map(int, stdin.split())
    if P > Q:
        P, Q = Q, P

    while True:
        # Calculate the current score
        P += Q
        Q = P - Q

        # Check if the game is over
        if P == 0 or Q == 0:
            if P == 0:
                return "paul"
            else:
                return "opponent"
        else:
            # Calculate the number of serves left in the current row
            N_left = N - Q

            # If there is at least one serve left, serve the ball
            if N_left > 0:
                return "paul"
            else:
                N = N_left
                P -= 1

