def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    throw_commands = list(map(int, stdin.strip().split()))

    children = list(range(n))
    for i in range(k):
        if throw_commands[i] == 0:
            children.pop(children.index(0))
        elif throw_commands[i] == n - 1:
            children.append(children.pop())
        else:
            throw_positions = [children.pop() - i for i in range(throw_commands[i])]
            children.extend(throw_positions)
            for _ in range(throw_commands[i] * 2):
                children.append(children.pop())

    for i in range(n - 1):
        if children[i] > children[i + 1]:
            return str(children[i])

    return str(children[-1])

