    incorrect_translations = {}

    for word in s.split():
        if word in dictionary:
            if word in correct_translations:
                correct_translations[word].append(dictionary[word])
            else:
                correct_translations[word] = [dictionary[word]]

    for word in s.split():
        if word in dictionary:
            if word in incorrect_translations:
                incorrect_translations[word].append(dictionary[word])
            else:
                incorrect_translations[word] = [dictionary[word]]

    result = []

    for word in s.split():
        if word in correct_translations:
            correct_count = len(correct_translations[word])
        else:
            correct_count = 0

        if word in incorrect_translations:
            incorrect_count = len(incorrect_translations[word])
        else:
