        result.append(f"{correct_count} {correct_translations[word][0] if correct_count > 0 else 'correct'}\n")
        result.append(f"{incorrect_count} {incorrect_translations[word][0] if incorrect_count > 0 else 'incorrect'}\n")

    return "\n".join(result)

