    holes.sort(key=lambda x: x[0])

    # cutting process
    thickness = 0
    for i in range(s):
        for j in range(n):
            if i < s - 1:
                if i == 0 or j == 0 or j == n - 1:
                    continue
                x1, y1, z1 = holes[j - 1]
                x2, y2, z2 = holes[j]
                dx = x2 - x1
                dy = y2 - y1
                dz = z2 - z1
                if abs(dz) < 1e-6:
                    continue
                d1 = dx * dy * dz
                d2 = dx * dz * dz
                if d1 < 0 and d2 < 0:
                    continue
                d = max(d1, d2)
                if d > thickness:
                    thickness = d
        thickness += 1

    # display result
    result = []
    for i in range(s):
        result.append(thickness)
    result.reverse()
    return''.join(str(round(x, 6)) for x in result)

