    area = max_area(r1, r2, r3, r4, 3)
    return str(area)

def solution(stdin: str) -> str:
    a, b = map(int, stdin.split())

    if b % 2 == 0:
        operations = 0
    else:
        operations = 1

    for _ in range(operations):
        a //= 2

    return str(operations)
