    def choose_student(students, distance, team):
        max_distance = distance(team[0], team[-1])
        min_distance = distance(team[0], team[0])
        for i in range(1, k):
            if distance(team[i], team[i-1]) > max_distance:
                min_distance = max_distance
                max_distance = distance(team[i], team[i-1])
            if distance(team[i], team[-1]) > max_distance:
                min_distance = max_distance
                max_distance = distance(team[i], team[-1])

        chosen_index = -1
        for i in range(k):
            if distance(team[i], team[i-1]) == min_distance and distance(team[i], team[-1]) == min_distance:
                chosen_index = i
                break
        return students[chosen_index]

    first_team = [students[students.index(x) - i] for i, x in enumerate(students, start=1)]
    second_team = [students[students.index(x) - i] for i, x in enumerate(students, start=k)]

    first_team.sort(key=lambda x: students.index(x))
    second_team.sort(key=lambda x: students.index(x))

    for _ in range(n):
        chosen_student = choose_student(first_team, distance, first_team)
        first_team.remove(chosen_student)
        first_team.insert(0, chosen_student)

        chosen_student = choose_student(second_team, distance, second_team)
        second_team.remove(chosen_student)
