    def distance(town):
        x1, y1 = town
        x2, y2 = x[1], y[1]
        return sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2)

    def paths(town):
        paths = []
        for i in range(2, n):
            if i == town:
                continue
            x1, y1 = x[i - 1], y[i - 1]
            x2, y2 = x[town], y[town]
            path = [(x1, y1)]
            while x1!= x2 or y1!= y2:
                x3, y3 = x1, y1
                x1, y1 = x2, y2
                x2, y2 = x3, y3
                path.append((x2, y2))
            path.append((x2, y2))
            paths.append(path)
        return paths

    def average_length(paths):
        total_length = sum(distance(p[0]) for p in paths)
        return total_length / len(paths)

    paths = paths(0)
    return str(average_length(paths))

