def solution(stdin: str) -> str:
    n, m = map(int, stdin.split())

    edges = [[] for _ in range(n)]

    for i in range(m):
        a, b = map(int, stdin.split())
        edges[a - 1].append((b - 1, i))
        edges[b - 1].append((a - 1, i))

    def is_cycle(edges, visited):
        for edge in edges:
            if len(visited) == len(set(edge)) and (edge[0] not in visited or edge[1] not in visited):
                return True
        return False

    def count_paths(visited):
        count = 0
        for i in range(n):
            if i == 1:
                continue
            if is_cycle(edges[i - 1], visited):
                count += 1
        return count

    return str(count_paths([1] * n))

