        return str(min(n - 1, n - 2))
    if n <= 2:
        return str(min(n - 1, n - 2))
    forest = [[] for _ in range(n)]
    visited = [False] * n
    queue = [(0, 0)]

    while queue:
        dist, start = queue.pop(0)
        if visited[start]:
            continue
        visited[start] = True
        for i, (x, y) in enumerate(edges):
            if not forest[x] and not forest[y]:
                forest[x].append(y)
                forest[y].append(x)
                queue.append((dist + 1, i))

    def dfs(node):
        longest = 1
        for child in forest[node]:
            if not visited[child]:
                visited[child] = True
                dist = dfs(child)
                if dist > longest:
                    longest = dist
        return longest

    for i in range(n - 1):
        if not visited[i]:
            visited[i] = True
            longest = dfs(i)
            if longest > n - 1 - i:
                return str(longest)

    return str(n - 1)

