                if dfs(v, visited, u, dist, MST, cost):
                    return True, cost

        return False, cost

    def union_find(n):
        parent = [-1] * n
        rank = [-1] * n

        def find(x):
            if parent[x]!= x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            x, y = find(x), find(y)
            if rank[x] < rank[y]:
                parent[x] = y
            elif rank[x] > rank[y]:
                parent[y] = x
            else:
                parent[y] = x
                rank[x] += 1

        return find, parent, rank

    min_operations = float('inf')
    MST = set()
    _, _, rank = union_find(n)

    for u in range(n):
        if not visited[u]:
            visited[u] = True
