    n, m = map(int, stdin.split())
    edges = set()
    for _ in range(m):
        v1, u1 = map(int, stdin.split())
        edges.add((v1, u1))
        edges.add((u1, v1))
    def has_cycle(graph, source, target):
        seen = set()
        queue = [(source, frozenset())]
        while queue:
            node, path = queue.pop(0)
            if node == target:
                return True
            for neighbor, neighbor_path in graph[node]:
                if neighbor not in seen:
                    seen.add(neighbor)
                    if frozenset(neighbor_path) & frozenset(path)!= frozenset():
                        queue.append((neighbor, path | frozenset(neighbor_path)))
        return False

    spanning_tree = []
    max_degree = 0
    for v in range(1, n+1):
        if v in edges:
            spanning_tree.append(v)
            max_degree = max(max_degree, len(edges.difference({v})))

    for v in range(1, n+1):
        if v not in edges and has_cycle({v}, v, n):
            spanning_tree.append(v)
            max_degree = max(max_degree, len(edges.difference({v})))

    for v in range(1, n+1):
        for u in range(v+1, n+1):
            if v!= u and (u, v) not in edges:
                if has_cycle({v}, u, n) or has_cycle({v}, v, u):
                    spanning_tree.append((v, u))
                    max_degree = max(max_degree, len(edges.difference({u, v})))

    return '\n'.join(map(str, spanning_tree))

