    # Find the minimum total cost
    total_cost = 0
    for i in range(n + 1):
        for j in range(i + 1, n + 1):
            total_cost += graph[i][j]

    # Find the minimum cost for the roads that cost zero
    min_road_cost = float('inf')
    for u in range(1, n + 1):
        for v in range(1, n + 1):
            if graph[u][v] == 0:
                min_road_cost = min(min_road_cost, graph[u][v])

    # Calculate the minimum total cost using the minimum cost for the roads that cost zero
    min_total_cost = total_cost - min_road_cost

    # Return the minimum total cost
    return str(min_total_cost)

