    # Write down all integers between 2 and N, inclusive
    primes = [2]
    for i in range(3, int(N**0.5) + 1, 2):
        while primes and primes[-1] * i > N:
            primes.pop()
        primes.append(i)

    # Find the smallest number not already crossed out and call it P
    P = primes[0]
    while P * P <= N:
        P += 1

    # Cross out P and all its multiples that aren't already crossed out
    crossed = set()
    while P * P <= N:
        if P not in crossed:
            crossed.add(P)
            for i in range(P * P, N + 1, P):
                if i not in crossed:
                    crossed.add(i)
                    N -= 1
                    if N == K:
                        return str(P)
        P += 1

    # If not all numbers have been crossed out, go to step 2
    return str(P)

