    n, k = map(int, stdin.split())
    samples = [stdin.split()[i:i+k] for i in range(0, n*k, k)]

    def longest_subsequence(s: str, t: str) -> str:
        i, j = 0, 0
        while i < len(s) and j < len(t):
            if s[i] == t[j]:
                i += 1
                j += 1
            elif s[i] > t[j]:
                j += 1
            else:
                i += 1
        return s[i:]

    def find_longest_subsequence(s: str, t: str) -> int:
        s = longest_subsequence(s, t)
        return len(s)

    tree = [(0, 0)]
    for i, sample in enumerate(samples):
        for j, dna in enumerate(sample):
            tree = [[w+1 for w in tree] for tree in tree]
            max_w = find_longest_subsequence(dna, sample[j+1:])
            tree[i+1][j+1] = (tree[i+1][j+1][0] + 1, max(tree[i+1][j+1][1], max_w))

    def max_tree_weight(tree: List[List[int]]) -> int:
        return sum(w for i, w in tree)

    min_w = float('inf')
    for tree in tree:
        w = max_tree_weight(tree)
        if w < min_w:
            min_w = w

    return min_w

