def solution(stdin: str) -> str:
    # Read the message from stdin
    message = stdin.strip()

    # Find the most dense matrix with at least 3 rows
    n = len(message)
    r, c = 0, 0
    for i in range(n):
        for j in range(i):
            if message[i] == message[j]:
                r += 1
                c = max(c, j + 1)

    # If there is no dense matrix with at least 3 rows, return the original message
    if r < 3:
        return message

    # Create an empty matrix with the most dense matrix with at least 3 rows
    matrix = [[0] * (c + 1) for _ in range(r + 1)]

    # Write the message into the matrix
    for i in range(r):
        for j in range(c):
            matrix[i][j] = ord(message[j]) - ord('a')

    # Encrypt the message using the most dense matrix with at least 3 rows
    encrypted_message = ''
    for i in range(r):
        row = ''
        for j in range(c):
            row += chr(matrix[i][j] % 26 + ord('a'))
        encrypted_message += row +''

    # Remove the last space
    encrypted_message = encrypted_message[:-1]

    return encrypted_message

