    if k < 1 or k > 1000000:
        return "Invalid input. Please enter a value between 1 and 1000000."

    bar_sizes = [2, 4, 8, 16, 32, 64, 128, 256, 512, 1024]

    bar_sizes.sort()

    min_size = bar_sizes[0]
    breaks = 0
    min_breaks = 0

    for bar_size in bar_sizes:
        if k % bar_size == 0:
            breaks += 1
            if breaks == k:
                break
            min_size = max(min_size, bar_size)
            min_breaks = min(min_breaks, breaks)

