def solution(stdin: str) -> str:
    word = stdin.split()[0]
    permutation = stdin.split()[1]

    def get_component(n: int) -> str:
        if n == 1:
            return "base"
        elif n == 2:
            return "vertical beam"
        elif n == 3:
            return "horizontal beam"
        elif n == 4:
            return "rope"
        elif n == 5:
            return "head"
        elif n == 6:
            return "torso"
        elif n == 7:
            return "right leg"
        elif n == 8:
            return "left leg"
        elif n == 9:
            return "right arm"
        elif n == 10:
            return "left arm"
        else:
            return "error"

    def has_letter(word: str, i: int) -> bool:
        return word[i].upper() in word

    def is_word_complete(word: str) -> bool:
        return len(word) == len(permutation)

    def play_game(word: str, permutation: str, winning_word: str, winning_permutation: str) -> str:
        win = False
        for i in range(len(word)):
            if has_letter(word, i):
                if word[i].upper() == permutation[i]:
                    word = word[:i] + word[i+1:]
                else:
                    word = word[:i] + get_component(permutation[i]) + word[i+1:]
            else:
                word = word[:i] + " " + word[i+1:]

        if is_word_complete(word):
            if word == winning_word:
                win = True
            else:
                win = False

        return win

    word = word.upper()
    permutation = permutation.upper()
    winning_word = word[::-1]
    winning_permutation = permutation[::-1]

    play_game(word, permutation, winning_word, winning_permutation)

    return "WIN" if winning_word == word else "LOSE"

