    def largest_digit_set(n: int) -> set:
        digits = set()
        while n > 0:
            digits.add(n % 10)
            n //= 10
        return digits

    def smallest_number(n: int, d: int) -> int:
        largest_set = largest_digit_set(n)
        smallest = n
        for i in range(len(largest_set)):
            if i > d:
                break
            if largest_set.issubset(set(str(i))):
                smallest = i
                break
        return smallest

    result = smallest_number(X, len(str(X)))
    return str(result)

