    # Read input
    N = int(stdin.strip())
    grid = []
    for _ in range(N):
        grid.append(stdin.strip())

    # Calculate Manhattan distance
    def manhattan_distance(a: str, b: str) -> int:
        x1, y1 = [ord(c) - ord('A') for c in a]
        x2, y2 = [ord(c) - ord('A') for c in b]
        dx, dy = y2 - y1, x2 - x1
        return abs(dx) + abs(dy)

    # Calculate distance from each city block to nearest safe house
    def distance_to_nearest_safe_house(city_blocks: List[str]) -> int:
        distances = {}
        for i, block in enumerate(city_blocks):
            if block == 'H':
                nearest_safe_house = city_blocks[:i+1]
                distances[block] = manhattan_distance(nearest_safe_house[0], nearest_safe_house[-1])
        return distances.get('H', 0)

    # Find maximum Manhattan distance
    max_distance = 0
    for i in range(N):
        for j in range(N):
            if grid[i][j] == 'S':
                distances = distance_to_nearest_safe_house([grid[i][j], grid[i][j+1], grid[i+1][j], grid[i+1][j+1]])
                if distances > max_distance:
                    max_distance = distances
    return max_distance

