        num_students = exams[room - 1][-1]

        for i in range(num_students):
            if i == num_students - 1:
                break
            rooms.append((room + 1, num_exams - 1))

    if len(rooms) == 0:
        return "impossible"

    safe_order = []
    for i in range(1, n):
        for j in range(i + 1, n):
            if i <= j and rooms[i] < rooms[j]:
                safe_order.append(i)
                safe_order.append(j)

    return''.join(map(str, safe_order))

