def solution(stdin: str) -> str:
    A, B, C, D = map(int, stdin.split())

    x = (A + B + C + D) / 2
    y = (A + B + C + D) / 4

    angle = 0
    dx, dy = 1, 0

    while angle!= 0:
        x += dx
        y += dy

        if angle == 90:
            dx, dy = 0, 1
        elif angle == 180:
            dx, dy = -1, 0
        elif angle == 270:
            dx, dy = 1, 0

        angle -= 90

    area = (x - D) * (y - C)

    return str(area)

