def solution(stdin: str) -> str:
    n, *divisors = list(map(int, stdin.split()))

    def merge_sort(arr: list) -> list:
        if len(arr) <= 1:
            return arr
        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])
        return merge(left, right)

    def merge(left: list, right: list) -> list:
        result = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1
        result.extend(left[i:])
        result.extend(right[j:])
        return result

    def binary_search(arr: list, target: int) -> int:
        left, right = 0, len(arr) - 1
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] == target:
                return mid
            elif arr[mid] < target:
                left = mid + 1
            else:
                right = mid - 1
        return -1

    sorted_divisors = merge_sort(divisors)
    x = 1
    while x <= n:
        if binary_search(sorted_divisors, x)!= -1:
            y = x + 1
            while y <= n:
                if binary_search(sorted_divisors, y)!= -1:
                    return f"{y} {x}"
                y += 1
        x += 1
    return "Impossible"

