        return max(row) // k

    row_sums = [[0] * m for _ in range(n)]
    for i in range(n):
        for j in range(m):
            row_sums[i][j] = max_sum(a[i][:j+1], k)

    max_sum_row = [0] * m
    for i in range(m):
        for j in range(n):
            max_sum_row[i] = max(max_sum_row[i], row_sums[j][i])

    return max(max_sum_row)

