    n, k = map(int, stdin.split())
    s = stdin.split()[1]

    # Create a set to store subsequences
    S = set()

    # Function to delete characters from a string
    def delete(s: str, i: int) -> str:
        return s[:i] + s[i+1:]

    # Function to check if a string is a subsequence of another string
    def is_subsequence(s1: str, s2: str) -> bool:
        return s2.startswith(s1)

    # Function to check if the set can be obtained with a specific length
    def can_construct(s: str, k: int, i: int) -> bool:
        if k == 0:
            return True
        if i == n:
            return False
        for j in range(i+1, n):
            # If the current character is a subsequence of the remaining part of the string
            if is_subsequence(s[i], s[:j]):
                # Check if we can include it in the set with the current cost
                if can_construct(s, k-1, j):
                    return True
        return False

    # Function to find the minimum possible total cost to obtain a set S of size k
    def min_cost(s: str, k: int) -> int:
        if k == 0:
            return 0
        min_cost = float('inf')
        for i in range(n):
            if i == 0:
                min_cost = min(min_cost, delete(s, i).count('a') * 10)
            else:
                min_cost = min(min_cost, delete(s, i).count('a') * 10 + min_cost)
        return min_cost

    # Check if the set can be obtained with a specific length
    if can_construct(s, k, 0):
        print(min_cost(s, k))
    else:
        print(-1)

