    def get_coordinates(coordinate: str) -> tuple:
        i, j = map(int, coordinate.split())
        return (i - 1, j - 1)

    def check_symmetric(matrix: list) -> bool:
        n = len(matrix)
        for i in range(n):
            for j in range(n):
                if matrix[i][j]!= matrix[j][i]:
                    return False
        return True

    def construct_square(tiles: list) -> bool:
        n = len(tiles)
        m = 2 ** n

        # Create a square matrix with n rows and m columns
        square = [[0] * m for _ in range(n)]

        # Place each tile
        for tile in tiles:
            i, j = get_coordinates(tile)
            square[i][j] = tile

        # Check if the square is symmetric
        if not check_symmetric(square):
            return False

        # Check if the square is filled with only one tile
        for row in square:
            if len(set(row)) > 1:
                return False

        return True

