def solution(stdin: str) -> str:
    memory = [0] * 30000
    memory_pointer = 0
    input_pointer = 0

    def read_input():
        nonlocal input_pointer
        return ord(stdin[input_pointer])

    def write_output(output: str):
        nonlocal memory, memory_pointer
        memory[memory_pointer] = ord(output)
        memory_pointer += 1

    def push(value: int):
        nonlocal memory, memory_pointer
        memory[memory_pointer] = value
        memory_pointer += 1

    def pop():
        nonlocal memory, memory_pointer
        return memory[memory_pointer - 1]

    def add():
        pop1 = pop()
        pop2 = pop()
        push(pop1 + pop2)

    def sub():
        pop1 = pop()
        pop2 = pop()
        push(pop2 - pop1)

    def read():
        value = read_input()
        push(value)

    def write():
        value = pop()
        write_output(chr(value))

    def loop():
        pop1 = pop()
        pop2 = pop()
        if pop1 == 0:
            program_pointer = pop2
        else:
            push(program_pointer)

    def inc():
        pop1 = pop()
        push(pop1 + 1)

    def dec():
        pop1 = pop()
        push(pop1 - 1)

    def jmp():
        pop1 = pop()
        program_pointer = pop1

    def jz():
        pop1 = pop()
        if pop1 == 0:
            program_pointer += 1

    def jnz():
        pop1 = pop()
        if pop1!= 0:
            program_pointer += 1

    commands = {
        '+': add,
        '-': sub,
        '>': write,
        '<': read,
        '[': loop,
        ']': jmp,
        ',': read,
        '.': write,
        '+>': add,
        '+<': dec,
        '-<': inc,
        '->': write,
        '<-': read,
        '?': jz,
        '!': jnz
    }

    program = []
    for char in stdin:
        if char == '+' or char == '-' or char == '>' or char == '<' or char == '[' or char == ']' or char == ',' or char == '.' or char == '+>' or char == '+<' or char == '-<' or char == '->' or char == '<-' or char == '?':
            program.append(char)
        elif char.isdigit():
            program.append(int(char))

    program_pointer = 0
    while program_pointer < len(program):
        command = program[program_pointer]
        if isinstance(command, int):
            write_output(chr(command))
        elif command in commands:
            commands[command]()
        program_pointer += 1

    return ''.join(chr(memory[i]) for i in range(memory_pointer))

