        return 1 < l <= 1000

    def extension(bamboo: int) -> int:
        if not is_valid(bamboo + 1):
            return -1
        l_bamboo[bamboo] += 1
        return 1

    def shortening(bamboo: int) -> int:
        if not is_valid(bamboo - 1) or l_bamboo[bamboo] < 2:
            return -1
        l_bamboo[bamboo] -= 1
        return 1

    def composition(bamboo1: int, bamboo2: int) -> int:
