            if not bamboos:
                return -1
            bamboo = bamboos.pop()
            if bamboo in used_bamboo_indices:
                continue
            used_bamboo_indices.add(bamboo)

            if bamboo_lengths[bamboo] == A:
                return 0

            if bamboo_lengths[bamboo] == B:
                if extension(bamboo)!= -1:
