    def find_time(index, speed):
        time = 0
        while index < n and time < k:
            if abs(candles[index] - 0) <= speed:
                time += speed
            index += 1
        return time

    left = 0
    right = n - 1
    min_time = float('inf')

    while left <= right:
        mid = (left + right) // 2
        time = find_time(mid, 1)

        if time < min_time:
            min_time = time
        elif time == min_time:
            min_time = min(min_time, mid)

        if time > k:
            left = mid + 1
        else:
            right = mid - 1

    return str(min_time)

