        for i in range(len(a) - 1):
            if abs(a[i] - a[i + 1]) > 1:
                return False
        return abs(a[0] - a[-1]) <= 1

    def max_balanced_circle(a: list) -> int:
        k = 0
        b = 1
        for _ in range(100):
            if is_balanced(a[:k + 1]):
                k += 1
                b *= k + 1
            else:
                b *= 2
        return b

    k = max_balanced_circle(a)
    max_a = max(a)
    res = [a.index(max_a)] * k
    b = 1
    for _ in range(k - 1):
        a = a[:k - 1] + [a[k - 1]] + a[k:]
        if is_balanced(a):
            k -= 1
            b /= k + 1
            res.append(a.index(max_a))
        else:
            b *= 2
    return f"{k}\n{res[::-1]}"

