    special_offers = [tuple(map(int, stdin.split())) for _ in range(m)]

    def buy_microtransactions(prices: List[int]) -> int:
        for price in prices:
            if price <= 2:
                return 1
            elif price == 1:
                return 2
        return 0

    def calculate_min_day(prices: List[int]) -> int:
        for day in range(1, 2 * n + 1, 2):
            for microtransaction in range(n):
                if k[microtransaction] == 0:
                    continue
                if day % special_offers[microtransaction][0] == 0:
                    prices[microtransaction] = 1
                elif day % special_offers[microtransaction][0] == 1:
                    prices[microtransaction] = 2
        for i in range(n):
            if prices[i] == 0:
                return 0
        return 1 + buy_microtransactions(prices)

    prices = [0] * n
    return str(calculate_min_day(prices))

