    n, m = map(int, stdin.split()[0:2])
    grid = [list(row.strip()) for row in stdin.split()[2:]]

    result = []
    def dfs(row, col, size, used_stars):
        if size == 0:
            return
        if row < 1 or row > n or col < 1 or col > m or grid[row - 1][col - 1] == '*' or (row, col) in used_stars:
            return
        used_stars.add((row, col))
        dfs(row - 1, col, size - 1, used_stars)
        dfs(row + 1, col, size - 1, used_stars)
        dfs(row, col - 1, size - 1, used_stars)
        dfs(row, col + 1, size - 1, used_stars)
        if size == 1:
            result.append((row, col, size))
        else:
            for i in range(4):
                dfs(row + (i - 1) // 2, col + (i - 1) % 2, size - 1, used_stars)

    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                dfs(i, j, 3, set())

    if result:
        return str(len(result)) + '\n' + '\n'.join([' '.join(map(str, row)) for row in result])
    else:
        return '-1\n'

