        for i, c in enumerate(colors):
            if c!= color:
                if recolored[i]:
                    result.append(c)
                else:
                    recolored[i] = True
                    result.append(c)
                    break

        # Step 4.3: If no recoloring was possible, check if the color is nice
        if len(result) >= n:
            for i in range(n - 1):
                if not (result[i] == result[i + 1] and abs(colors.index(result[i]) - colors.index(result[i + 1])) % 3 == 0):
                    break
            else:
                break

    # Step 5: If no recoloring was possible, return the initial garland
    if len(result) < n:
        result = colors

    # Step 6: Return the result string
    return f"{len(result)} {''.join(result)}"

