        lo, hi = 0, len(coins)
        while lo < hi:
            mid = (lo + hi) // 2
            if coins[mid] < a:
                lo = mid + 1
            elif coins[mid] > b:
                hi = mid
            else:
                return mid
        return lo

    def dfs(start, target, memo):
        if start == target:
            return 0
        if (start, target) in memo:
            return memo[(start, target)]

        min_coins = float('inf')
        for i in range(start, len(coins)):
            if coins[i] < target:
                min_coins = min(min_coins, dfs(i + 1, target - coins[i], memo))

        memo[(start, target)] = min_coins
        return min_coins

    ans = [-1] * q
    memo = {}
    for i in range(q):
        b = int(stdin.split()[1])
        ans[i] = dfs(0, b, memo)

    return '\n'.join(map(str, ans))


