    def gcd(a: int, b: int) -> int:
        while b:
            a, b = b, a % b
        return a

    def mod_power(a: int, n: int, mod: int) -> int:
        res = 1
        a %= mod
        while n > 0:
            if n & 1:
                res = (res * a) % mod
            a = (a * a) % mod
            n >>= 1
        return res

    def gcd_mod(a: int, b: int, mod: int) -> int:
        return (a * mod_power(b, mod - 2, mod)) % mod

    def mod_inverse(a: int, mod: int) -> int:
        return mod_power(a, mod - 2, mod)

    def modular_multiplication(a: int, b: int, mod: int) -> int:
        return (a * b) % mod

    def modular_division(a: int, b: int, mod: int) -> int:
        return (a * mod_inverse(b, mod)) % mod

    def modular_remainder(a: int, b: int, mod: int) -> int:
        return (a - modular_division(a, b, mod) * b) % mod

    def modular_addition(a: int, b: int, mod: int) -> int:
        return (a + b) % mod

    def modular_subtraction(a: int, b: int, mod: int) -> int:
        return modular_addition(a, -b, mod)

    def modular_power(a: int, n: int, mod: int) -> int:
        if n == 0:
            return 1
        elif n % 2 == 0:
            x = modular_power(a, n // 2, mod)
            return x * x % mod
        else:
            return (a * modular_power(a, n - 1, mod)) % mod

    def modular_gcd(a: int, b: int, mod: int) -> int:
        return modular_power(modular_inverse(b, mod), modular_subtraction(mod, 1, mod), mod)

    def find_k(x: int, y: int, n: int) -> int:
