            return y

        a = modular_gcd(x, y, n)
        if a == 1:
            return 0

        z = modular_inverse(x, n)
        t = modular_remainder(y, x, n)
        r = n

        while t!= 0:
            q = modular_division(t, r, n)
            r = modular_subtraction(r, q * r, n)
            t = modular_subtraction(t, q * t, n)

        return modular_addition(modular_remainder(r, n, n), 1, n)

