        return max(p[0], p[1])

    def is_visited(p: Tuple[int, int], visited: Set[Tuple[int, int]]) -> bool:
        return p in visited

    def is_level_visited(level: int, visited: Set[int]) -> bool:
        return level in visited

    def bfs(start: Tuple[int, int], goal: Tuple[int, int], visited: Set[Tuple[int, int]], level: int) -> int:
        visited.add(start)
        queue = [(start, 0)]
        while queue:
            current, steps = queue.pop(0)
            if current == goal:
                return steps
            for next_ in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                next_ = (current[0] + next_[0], current[1] + next_[1])
                if not is_visited(next_, visited) and distance(current, next_) <= level:
                    visited.add(next_)
                    queue.append((next_, steps + 1))
        return -1

    n = int(stdin.split()[0])
    points = set()
    for _ in range(n):
        x, y = map(int, stdin.split()[1:])
        points.add((x, y))

    levels = sorted(set(level(p) for p in points))
    visited = set()
    min_distance = float('inf')

    for level_ in levels:
        if is_level_visited(level_, visited):
            continue
        min_distance = min(min_distance, bfs((0, 0), (level_, 0), visited, level_))

    return str(min_distance)

