def solution(stdin: str) -> str:
    N, *points = map(int, stdin.split())

    def distance(a: tuple, b: tuple) -> int:
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def min_distance(points: list) -> int:
        return min(distance(points[i], points[j]) for i in range(len(points)) for j in range(i + 1, len(points)))

    min_distance_0 = min_distance(points)

    def min_distance_i(i: int, points: list) -> int:
        return min(distance(points[i], points[j]) for j in range(i, len(points)))

    def min_distance_i_minus_1(i: int, points: list) -> int:
        return min(distance(points[i], points[j]) for j in range(i - 1, -1, -1))

    def cancel_visit(i: int, points: list) -> int:
        return min_distance_0 + min_distance_i_minus_1(i, points)

    total_cost = sum(cancel_visit(i, points) for i in range(1, N + 1))

    return f"{total_cost}\n" * N

