def solution(stdin: str) -> str:
    n, *lines = stdin.split('\n')
    n = int(n)
    ratios = []
    for i, line in enumerate(lines):
        t, a = map(int, line.split())
        ratios.append((t, a))

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    for t, a in ratios:
        t %= 1000
        a %= 1000
        t *= n
        a *= n
        if gcd(t, a)!= 1:
            return "IMPOSSIBLE"

    takahashi = ratios[0][0]
    aoki = ratios[0][1]
    for t, a in ratios[1:]:
        takahashi += t
        aoki += a
        takahashi %= 1000
        aoki %= 1000
        takahashi *= n
        aoki *= n
        if gcd(takahashi, aoki)!= 1:
            return "IMPOSSIBLE"

    return min(takahashi, aoki)

