    leader_index = N // 2
    directions = [S[leader_index]] * N

    def get_neighbors(index: int, direction: str) -> list:
        neighbors = []
        for i in range(1, N):
            if i!= leader_index and directions[i] == direction:
                neighbors.append(i)
        return neighbors

    leader_direction = directions[leader_index]
    leader_neighbors = get_neighbors(leader_index, leader_direction)
    leader_neighbors.append(leader_index)

    for i in range(N):
        if directions[i] == leader_direction:
            continue

        neighbors = get_neighbors(i, directions[i])
        for neighbor in neighbors:
            leader_neighbors.remove(neighbor)

        if len(leader_neighbors) == 0:
            return i + 1

    return len(leader_neighbors)

