    packages.append((0, 0))

    visited = set()
    pq = []

    def bfs():
        global visited, pq
        visited.add((0, 0))
        heapq.heappush(pq, (0, (0, 0), (0, 0)))

        while pq:
            dist, pos, current = heapq.heappop(pq)

            x, y = pos

            if x == n-1 and y == n-1:
                return dist
            elif x == n-1:
                heapq.heappush(pq, (dist+1, (x, y+1), (x+1, y)))
            elif y == n-1:
                heapq.heappush(pq, (dist+1, (x+1, y), (x, y+1)))

            for dx, dy in [(1, 0), (0, 1), (-1, 0), (0, -1)]:
                nx, ny = x+dx, y+dy
                if 0 <= nx < n and 0 <= ny < n and (nx, ny) not in visited and packages[nx][ny] == 0:
                    heapq.heappush(pq, (dist+1, (nx, ny), (nx, ny)))

    ans = 'YES'
    if n > 1:
        dist = bfs()
        if dist == float('inf'):
            ans = 'NO'
        else:
            path = []
            x, y = 0, 0
            while True:
                nx, ny = x+1, y
                if 0 <= nx < n and 0 <= ny < n and (nx, ny) in visited:
                    break
                path.append(('R', (x, y), (nx, ny)))
                x, y = nx, ny
            x, y = n-1, n-1
            while True:
                nx, ny = x-1, y
                if 0 <= nx < n and 0 <= ny < n and (nx, ny) in visited:
                    break
                path.append(('U', (x, y), (nx, ny)))
                x, y = nx, ny
            path.reverse()
            ans += ''.join(map(lambda p: 'R' if p[0] == 'R' else 'U', path))

