def solution(stdin: str) -> str:
    n, p = map(int, stdin.split())
    p = list(map(int, p.split()))

    def merge_sort(arr):
        if len(arr) <= 1:
            return arr

        mid = len(arr) // 2
        left = merge_sort(arr[:mid])
        right = merge_sort(arr[mid:])

        return merge(left, right)

    def merge(left, right):
        result = []
        i, j = 0, 0

        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                result.append(left[i])
                i += 1
            else:
                result.append(right[j])
                j += 1

        result += left[i:]
        result += right[j:]

        return result

    operations = []
    for _ in range(n - 1):
        operation = map(int, stdin.split())
        operations.append(operation)

    operations.sort(key=lambda x: (x[0] - 1, x[1] - 1))

    min_permutation = p.copy()
    for operation in operations:
        i, j = operation[0] - 1, operation[1] - 1
        min_permutation[i], min_permutation[j] = min_permutation[j], min_permutation[i]

    return " ".join(map(str, min_permutation))

