    def find_next_student(i):
        while i < n:
            i += 1
            if students[i] > students[i - 1]:
                return i
        return n

    def is_valid_sequence(students):
        for i in range(2, n + 1):
            next_student = find_next_student(i)
            if next_student > i:
                return False
        return True

    def start_clockwise_round(students):
        next_student = find_next_student(2)
        if next_student > 2:
            return False
        for i in range(2, n + 1):
            next_student = find_next_student(i)
            if next_student <= i - 1 or students[i]!= students[next_student - 1]:
                return False
        return True

    def start_counterclockwise_round(students):
        next_student = find_next_student(n)
        if next_student > n:
            return False
        for i in range(n - 1, 1, -1):
            next_student = find_next_student(i)
            if next_student < i + 1 or students[i]!= students[next_student + 1]:
                return False
        return True

    for _ in range(q):
        query = list(map(int, stdin.split()))
        if is_valid_sequence(query):
            print("YES")
        elif start_clockwise_round(query):
            print("YES")
        elif start_counterclockwise_round(query):
