def solution(stdin: str) -> str:
    n, m = map(int, stdin.split())
    streets = [[] for _ in range(n * m)]

    for _ in range(n):
        row = list(map(int, input().split()))
        for j in range(m):
            streets[row[j] * m + j].append(True)

    # BFS to find the number of blocks
    queue = [(0, 0, 1)]
    num_blocks = 0

    while queue:
        x, y, block_size = queue.pop(0)

        # Check if current block has the smallest size
        if (x == 0 and y == n - 1 and block_size == n) or (x == m - 1 and y == 0 and block_size == m):
            num_blocks += 1

        # Check neighboring blocks
        for dx, dy in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
            nx, ny = x + dx, y + dy

            # Skip out-of-bounds streets
            if nx < 0 or nx >= n or ny < 0 or ny >= m:
                continue

            # Skip already visited streets
            if streets[nx * m + ny]:
                continue

            # Add the current block to the queue
            queue.append((nx, ny, block_size + 1))
            streets[nx * m + ny] = True

    return str(num_blocks)

