        while head is not None:
            print(f"{head.val} ", end="")
            head = head.next
        print()

    # Function to detect a cycle in the linked list
    def detect_cycle(head: ListNode) -> bool:
        slow = head
        fast = head

        while fast is not None and fast.next is not None:
            slow = slow.next
            fast = fast.next.next

            if slow == fast:
                return True

        return False

    # Function to detect if a node is the tail of a cycle
    def is_tail(node: ListNode, pos: int) -> bool:
        current = node
        while current is not None:
            if current.next is None or current.next == node:
                return True
            current = current.next

        return False

    # Function to find the position of the tail node
    def find_tail_pos(head: ListNode, pos: int) -> int:
        current = head
        tail_pos = 0

        while current is not None:
            if current.next is None or current.next == head:
                return tail_pos
            tail_pos += 1
            current = current.next

        return -1

    # Initialize the head of the linked list
    head_node = ListNode(head)

    # Print the original linked list
    print_linked_list(head_node)

    # Detect a cycle in the linked list
    cycle_exists = detect_cycle(head_node)

    # Find the position of the tail node
    if pos == -1:
        tail_pos = find_tail_pos(head_node, 0)
    else:
        tail_pos = pos

    # Print the linked list after removing the cycle
    if cycle_exists:
        if tail_pos == 0:
            head_node.next = None
        else:
            current = head_node
            i = 0

            while i < tail_pos:
                current = current.next
                i += 1

            current.next = None
