def solution(stdin: str) -> str:
    # Parse input
    rows, cols = map(int, stdin.split()[0].split(" "))
    board = []
    for _ in range(rows):
        row = list(stdin.split()[1])
        board.append([char == "#" for char in row])

    # Find the lexicographically smallest word
    queue = []
    for i in range(rows):
        for j in range(cols):
            if board[i][j]:
                queue.append((i, j))

    visited = set()
    while queue:
        i, j = queue.pop(0)
        if i == 0 or board[i - 1][j]:
            for direction in [(0, -1), (1, 0), (0, 1), (-1, 0)]:
                x, y = i, j
                while 0 <= x < rows and 0 <= y < cols and not board[x][y]:
                    visited.add((x, y))
                    x, y = x + direction[0], y + direction[1]
                if (x, y) in visited:
                    return ""
                if board[x][y]:
                    break
        visited.add((i, j))

    for direction in [(0, -1), (1, 0), (0, 1), (-1, 0)]:
        x, y = i, j
        while 0 <= x < rows and 0 <= y < cols and board[x][y]:
            visited.add((x, y))
            x, y = x + direction[0], y + direction[1]

    return "".join(["".join(["" if not board[i][j] else char for j in range(cols)]) for i in range(rows)])

