    u, l, r, d = map(int, stdin.split()[1].split())

    # input the crossword puzzle
    puzzle = [list(row) for row in stdin.split()[2:]]

    # create the frame
    frame = ['#' for _ in range(u + d + 2)]
    for i in range(u):
        frame[i] = '.'
    for j in range(l):
        frame.insert(1, '.')
    for j in range(m - n + l, m):
        frame.append('.')
    for i in range(m - n + r + 1, m):
        frame.append('.')
    for i in range(u, m - n + d):
        frame[i + 1] = '.'

    # frame the puzzle
    for i in range(m):
        for j in range(n):
            if puzzle[i][j] == '#':
                frame[u + d - i + 1][j + l] = '#'
                frame[u + d - i + 1][j + l + 1] = '#'
                frame[u + d - i + 1][j + l + 2] = '#'

    # output the framed crossword puzzle
    return '\n'.join(''.join(row) for row in frame)

